mapper.select:
SELECT m.*,m2.*
from t_wxcms_fans_tixian m,t_wxcms_account_fans m2
WHERE  m2.id=m.fansId

    and  m.fansId = #{aram1.fansId}

 order by m.id desc
LIMIT #{param2.start} , #{param2.pageSize}

mapper.update:
